mapper.select:
SELECT * FROM (
    SELECT judgement_pk, article_pk, board_pk, title, created_dttm
    FROM tbl_judgement
    WHERE member_pk = #{memberPk}
    AND judgement_pk < #{lastPk}
    ORDER BY judgement_pk DESC
    )
WHERE ROWNUM <= #{size}+1

SELECT * FROM (
    SELECT judgement_pk, article_pk, board_pk, title, created_dttm
    FROM (
        SELECT judgement_pk, article_pk, board_pk, title, created_dttm
        FROM tbl_judgement
        WHERE member_pk = #{memberPk}
        ORDER BY judgement_pk DESC
        )
    WHERE ROWNUM <= #{size}+1
)
